import React, { Component } from 'react';
import Button from 'react-bootstrap/ToggleButton';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const dayjs = require('dayjs')

class EmployeePage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: props.employee_id,
            name: null,
            lastname: null,
            clockedin: false
        }
    }

    async componentDidMount() {
        const currentDate = dayjs().format('YYYY-MM-DD');
        const response = await axios.put(this.props.server+"/employee",{employee_id: this.state.id, employee_date: currentDate});
        this.setState({
            name: response.data[0].employee_name,
            lastname: response.data[0].employee_surname,
        });
    }

    clockIn = async () => {
        const currentDate = dayjs().format('YYYY-MM-DD');
        const currentTime = dayjs().format('YYYY-MM-DD hh:mm:ss');
        const response = await axios.put(this.props.server+"/employee/clock-in",{employee_id: this.state.id, 
            employee_date: currentDate, employee_clock_in: currentTime});
        console.log(response.data.message);
        this.setState({
            clockedin: true
        });
    }

    clockOut = async () => {
        const currentDate = dayjs().format('YYYY-MM-DD');
        const currentTime = dayjs().format('YYYY-MM-DD hh:mm:ss');
        const response = await axios.put(this.props.server+"/employee/clock-out",{employee_id: this.state.id, 
            employee_date: currentDate, employee_clock_out: currentTime});
        console.log(response.data.message);
        this.setState({
            clockedin: false
        });
    }

    render() {
        if (!this.state.clockedin) {
            return (
                <>
                  <h3>Hello,{this.state.id} {this.state.lastname}</h3>
                  <Button onClick={this.props.logOutFunc}>Log Out</Button>
                  <Button onClick={this.clockIn} variant="primary">Clock-In</Button>
                </>
              );
        }
        else {
            return (
                <>
                  <h3>Hello,{this.state.id} {this.state.lastname}</h3>
                  <Button onClick={this.clockOut} variant="primary">Clock-Out</Button>
                </>
            );
        }
    }
}

export default EmployeePage;
