const bcrypt = require("bcryptjs");

async function hashPassword(password) {
    const hashed = await bcrypt.hash(password, 10);
    return hashed; 
}

async function comparePasswords(plaintext, hash) {
    return await bcrypt.compare(plaintext, hash);
}

module.exports = {
    hashPassword,
    comparePasswords
}
