import { useState, useEffect } from 'react';
import React, { Component } from 'react';
import LogInPage from './LogInPage';
import "./App.css"
import Button from 'react-bootstrap/ToggleButton';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { TextField } from '@mui/material';

const serverRoot = "http://localhost:3300";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loggedIn: false,
      currentUserId: null,
      managerStatus: null
    }
  }

  logIn = (userId, manstatus) => {
     this.setState({
      loggedIn: true,
      currentUserId: userId,
      managerStatus: manstatus
     })
  }

  logOut = () => {
    this.setState({
      loggedIn: false,
      currentUserId: null,
      managerStatus: null
    })
  }

  render() {
    if (!this.state.loggedIn) {
      return (
      <>
      <LogInPage server={serverRoot} logIn={this.logIn} />
      </>
      )
    }
    else {
      return (
        <>
          <h1>Logged In</h1>
          <h3>{this.state.currentUserId}</h3>
          <h3>{this.state.managerStatus}</h3>
          <Button onClick={this.logOut}>Log Out</Button>
        </>
      );
    }
  }
}

export default App;
