import React, { Component } from 'react';
import { Button, Form, Table} from 'react-bootstrap';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const dayjs = require('dayjs')

class ManagerPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: props.employee_id,
            employees: [],
            name: null,
            lastname: null,
            currentEmployeeId: null,
        }
    }

    async componentDidMount() {
        const firstResponse = await axios.put(this.props.server + "/manager", { employee_id: this.state.id });
        const response = await axios.put(this.props.server + "/manager/employee-list");
        this.setState({
            name: firstResponse.data[0].employee_name,
            lastname: firstResponse.data[0].employee_surname,
            employees: response.data
        });
    }

    tableGenerator() {
        const ret = [];
        console.log(this.state.employees)
        for (var i = 0; i < this.state.employees.length; i++) {
            console.log()
            ret.push(<tr>
                <td>{this.state.employees[i].employee_id}</td>
                <td>{this.state.employees[i].employee_name}</td>
                <td>{this.state.employees[i].employee_surname}</td>
                </tr>);
        }
        return ret;
    }
    render() {
        return (
            <>
                <h3>Hello, {this.state.name} {this.state.lastname}</h3>
                <div><Button onClick={this.props.logOutFunc}>Log out</Button></div>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Employee #</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                        </tr>
                    </thead>
                    <tbody></tbody>
                    {this.tableGenerator()}
                </Table>
            </>
        );
    }
}

export default ManagerPage;
